/* eslint-disable import/no-anonymous-default-export */
import { useParams } from "react-router-dom";
import { retrieveTodoApi } from "./api/TodoApiService";
import { useAuth } from "./security/AuthContext";
import { useEffect, useState } from "react";
import { Field, Form, Formik } from "formik";

export default function () {
  const { id } = useParams();
  const authContext = useAuth();
  const username = authContext.username;

  const [description, setDescription] = useState("");
  const [targetDate, setTargetDate] = useState("");
  useEffect(() => retrieveTodo(), [id]); // [] to run the effect only once when the component mounts

  function retrieveTodo() {
    retrieveTodoApi(username, id)
      .then((response) => setDescription(response.data.description))
      .then((response) => setTargetDate(response.data.targetDate))
      .catch((error) => console.error("Error:", error));
  }
  return (
    <div className="container">
      <h1>Todo Component</h1>
      <div>
      {/* We need to pass the initialValues here and reinitialize to true to refresh the form */}
        <Formik initialValues={{description, targetDate}} enableReinitialize={true} > 
          {(props) => (
            <Form>
              <fieldset className="form-group">
                <label>Description</label>
                <Field type="text" className="form-control" name="description" />
              </fieldset>
              <fieldset className="form-group">
                <label>Target Date</label>
                <Field type="date" className="form-control" name="targetDate" />
              </fieldset>
              <></>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}
