import {useState} from "react";
import "./Counter.css";

import propTypes from 'prop-types';


export default function Counter() {
  const [count, setCount] = useState(0);

  function increaseCounterParentFunction(by){
    setCount(count + by);
  }
    return (
        <>
         <span className="totalCount">{count}</span>
            <CounterButton incrementMethod={increaseCounterParentFunction}/>
            <CounterButton by={2} incrementMethod={increaseCounterParentFunction}/>
            <CounterButton by={5} incrementMethod={increaseCounterParentFunction}/>
        </>


    )

}

function CounterButton({by, incrementMethod}) {
    const [count, setCount] = useState(0);


    function incrementCounterFunction() {
        setCount(count + by);
        incrementMethod();
    }

    function decrementCounterFunction() {
        setCount(count - by);
        //console.log(count);
    }


    return (
        <div className="Counter">
           
            <button className="CounterButton" onClick={incrementCounterFunction}>
                +{by}
            </button>
            <button className="CounterButton" onClick={decrementCounterFunction}>
                -{by}
            </button>
        </div>
    );
}

CounterButton.propTypes = {
    by: propTypes.number,
};

CounterButton.defaultProps = {
    by: 1,
};
