import { useEffect, useState } from "react";
import { retrieveTodosWithUsers } from "./api/TodoApiService";

function ListTodosComponent() {
  const today = new Date();
  const targetDate = new Date(
    today.getFullYear() + 12,
    today.getMonth(),
    today.getDay()
  );

  const [todos, setTodos] = useState([]);

  useEffect(() => refreshTodos(), []);

  function refreshTodos() {
    retrieveAllTodosWithUserName("john")
      .then((response) => {
        console.log("API Response:", response); // Gelen yanıtı kontrol etmek için
        setTodos(response.data); // Doğrudan `data` alanını kullan
      })
      .catch((error) => console.error("Hata:", error));
  }

  function deleteTodo(id) {
    console.log("DeleteTodo is called " + id);
  }
  // const todos = [
  //   {
  //     id: 1,
  //     description: "Learn React",
  //     done: false,
  //     targetDate: targetDate,
  //   },
  //   {
  //     id: 2,
  //     description: "Learn Angular",
  //     done: false,
  //     targetDate: targetDate,
  //   },
  //   {
  //     id: 3,
  //     description: "Learn Vue",
  //     done: false,
  //     targetDate: targetDate,
  //   },
  // ];
  return (
    <div className="container">
      <h1>
        <i>List of things to do.</i>
      </h1>
      <table className="table">
        <thead>
          <tr>
            <th>Descripcion</th>
            <th>Is done?</th>
            <th>Target Date</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.description}</td>
              <td>{todo.done.toString()}</td>
              <td>{todo.targetDate.toString()}</td>
              <td>
                <button className="btn btn-danger" onClick={() => deleteTodo(todo.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListTodosComponent;
